+ binding.textViewTipoDescricao.text = tipo.descricao
+
+ binding.buttonEdit.setOnClickListener {
+ onTipoClick(tipo, ACTION_EDIT)
+ }
+
+ binding.buttonDelete.setOnClickListener {
+ onTipoClick(tipo, ACTION_DELETE)
+ }
+ }
+ }
+}
